package com.data;

public class LoopingCondition {

	public static void main(String[] args) {

		// TODO Auto-generated method stub
for (int i = 0; i < 4; i++) {
	System.out.print(i+"  ");
	
}
int i=0;
System.out.println("\n"+"While loop start");
while (i<7) {
	i++;
	System.out.println(i);
}
System.out.println("do -while loop start");
int j=0;
do {
	j++;
	System.out.println(j);
} while (j<10);

	}

}
